        const {list, obj} = res.data
            const {date1, num1 = 1, num2, ...objRest} = obj 
        const formData = {
            list: list?.map(item => {
                const {p1, ...itemRest} = item
                    return {
                    ...itemRest,
                        p2: p1
                    }
                }),
                obj: {
