package main

import (
"fmt"
"sync"
)

func main() {
var wg sync.WaitGroup

intSlice := []int{1, 2, 3, 4, 5}
wg.Add(len(intSlice))

v1, v2 := 0, 0
for _, v := range intSlice {
vv := v
go func() {
defer wg.Done()
v1 += v
v2 += vv
}()
}
wg.Wait()
fmt.Printf("v1:%v, v2:%v \n", v1, v2)
}
