    do {
        try udpSocket?.bind(toPort: UInt16(port), interface: udpError)
        try udpSocket?.enableBroadcast(true)
        try udpSocket?.beginReceiving()
        self.isStarting = true
    } catch {
        print("catch:\(String(describing: error))")
