   if ( $uri ~* "\.(gif|png|jpg|css|js|woff|woff2)$" )
    {
         proxy_ignore_headers Set-Cookie Cache-Control expires;
         proxy_cache cache_one;
         proxy_cache_key $host$uri$is_args$args;
         proxy_cache_valid 200 304 301 302 10080m;
    }


}

