#include<vector>
using namespace std;

int main(){

    vector<int> a;
    int cap = a.capacity();
    cout<<cap<<endl;
    for(int i = 0; i<=cap;i++){
        a.push_back(1);
    }
    cout<<a.capacity()<<endl;

    return 0;
}

