import { useState } from "react"

function App() {
  let [personList, setPersonList] = useState([{ name: "jack", age: 18 }])

  const onClick1 = () => {
    let newState = [...personList]
    newState.push({ name: "kk", age: 1 })
    // ËøôÈáåÁõ∏ÂΩì‰∫éÁõ¥Êé•‰øÆÊîπ‰∫Ü personList[0].name ‰∏äÁöÑÂÄºÔºåÂØπ‰∫é personList Ëøô‰∏™ state Ê≤°ÊúâÂÅöÂà∞ immutable
    newState[0].name = "ddddddd"
    setPersonList(newState)
  }

  const onClick2 = () => {
    setPersonList([
      {
        // ËøôÈáåÂÖàÂ§çÂà∂‰∏ÄÈÅç personList[0]ÔºåÂÜçÂ§çÂà∂ÔºåpersonList Ëøô‰∏™ state Âú®ËøáÁ®ã‰∏≠ÊòØÂè™ËØªÁöÑÔºåÊòØ immutable
        ...personList[0],
        name: "ddddddd",
      },
      ...personList.slice(1),
      { name: "kk", age: 1 },
    ])
  }

  return (
    <div className="App">
      <button onClick={onClick1}>add1</button>
      <button onClick={onClick2}>add2</button>
      {personList.map((item, index) => {
        return <div key={index}>{item.name}</div>
      })}
    </div>
  )
}

export default App

