const hooks = [];
function useState(val) {
  let state = hooks[0] || val;
  hooks[0] = state;
  function setVal(v) {
    state = v;
    hooks[0] = state;
  }
  return [state, setVal];
}
let cleanup = null;
function useEffect(callback) {
  if (cleanup) cleanup()
  cleanup = callback();
}
function Foo() {
  const [count, setCount] = useState(0);
  useEffect(() => {
    setTimeout(() => { console.log(count)}, 1000);
  })
  return setCount;
}
var setCount = Foo(); // log 0
setCount(1);
Foo(); // log 1

