import ( "encoding/xml" "fmt" "os" )
type Client struct { Id string `xml:"client>id"` Pwd string `xml:"client>pwd"` Serviceid string `xml:"client>serviceid"` }
type Sms_info struct { Phone string `xml:"sms_info>phone"` Content string `xml:"sms_info>content"` }
type Sms struct { XMLName xml.Name `xml:"sms"` Ver string `xml:"ver,attr"` Client Sms_info }
type Svc_init struct { XMLName xml.Name `xml:"svc_init"` Ver string `xml:"ver,attr"` Sms }
func main() { id := "id" pwd := "pwd" serviceid := "serviceid"
client := Client{Id: id, Pwd: pwd, Serviceid: serviceid} sms_info := Sms_info{Phone: "13013001300", Content: "ÊµãËØï"}
sms := Sms{Ver: "2.0.0", Client: client, Sms_info: sms_info}
v := Svc_init{Ver: "2.0.0", Sms: sms}
fmt.Println(v) output, _ := xml.MarshalIndent(v, " ", " ")
os.Stdout.Write(output) }
