#include <iostream>
#include <math.h>

using namespace std;

int64_t random(int64_t seed){
    uint64_t result;
    result = (seed * 33 + 49297) % 23680;
    result = ceil((result * 10000000) / 23680);
    return result;
}


int main(){
    cout << random(1) << endl;
}

输出：
