package main

import (
    "fmt"
    "sync"
)

func main() {
    var wg sync.WaitGroup = sync.WaitGroup{}
    wg.Add(1)

    // var ch chan bool = make(chan bool)
    var i int = 0

    go func() {
        for {
            // è‡ªæ—‹é”
            for i%3 != 0 {
            }

            fmt.Print("A", i)

            i = i + 1
        }
    }()
    go func() {
        for {
            // è‡ªæ—‹é”
            for i%3 != 1 {
            }

            fmt.Print("B", i)

            i = i + 1
        }
    }()
    go func() {
        for {

// é™åˆ¶å¾ªç¯æ¬¡æ•°ï¼Œé¿å…ä¸€ç›´æ­»å¾ªç¯
            if i >= 3 {
                fmt.Print("E", i, "\n")
                i = 2
                break
            }

            // è¿™æ®µå¦‚æœæ³¨é‡Šæ‰ï¼Œå°±åªä¼šè¾“å‡º AB ç„¶åä¸€ç›´æ­»å¾ªç¯
            fmt.Print("[K]")

            // è‡ªæ—‹é”
            for i%3 != 2 {
            }

            fmt.Print("C", i)

            i++

        }
        wg.Done()
    }()

    // ch <- true

    wg.Wait()
}


