    switch (e.key){
        case "ArrowLeft":
            dx=0;
            break;
        case "ArrowRight":
            dx=0;
            break;
        case "ArrowUp":
            dy=0;
            break;
        case "ArrowDown":
            dy=0;
            break;
    }
}


