function init(fn){
    document.onkeydown=(e)=>{
        if(e.key==="Enter"){
            fn(true);
        }else if(e.key==="Escape"){
            fn(false);
        }
    }
}

let fn=(arg)=>{
    if(arg){
        console.log("enter");
    }else{
        console.log("esc");
        init(fn);
    }
}

init(fn)

